//Calculate Time Difference
function timeDifference(current, previous) {				
	var msPerMinute = 60 * 1000;
	var msPerHour = msPerMinute * 60;
	var msPerDay = msPerHour * 24;
	var msPerWeek = msPerDay * 7;
	var msPerMonth = msPerDay * 30;
	var msPerYear = msPerDay * 365;
	
	var elapsed = current - previous.getTime() + new Date().getTimezoneOffset()*msPerMinute;
	
	if (elapsed < msPerMinute) {
		 return Math.floor(elapsed/1000) + 's';	
	}
	
	else if (elapsed < msPerHour) {
		 return Math.floor(elapsed/msPerMinute) + 'm';	
	}
	
	else if (elapsed < msPerDay ) {
		 return Math.floor(elapsed/msPerHour ) + 'h';	
	}

	else if (elapsed < msPerWeek) {
		 return Math.floor(elapsed/msPerDay) + 'd';	
	}
	
	else {
		 return Math.floor(elapsed/msPerWeek) + 'w';  
	}
}

