function sub(id){
	var cid = "c" + id;
	var gid = "g" + id;
	var caret = document.getElementById(cid);
	var group = document.getElementById(gid);
	if (group.hidden){
		group.hidden = false;
		caret.classList.add('fa-caret-down');
		caret.classList.remove('fa-caret-right');
	} else {
		group.hidden = true;
		caret.classList.add('fa-caret-right');
		caret.classList.remove('fa-caret-down');
	}
}

var dark_mode = Number(getPref("dark_mode"));
function theme(t, s){
	t = t || 0; //0 = Toggle; 1 = Light; 2 = Dark
	s = s || 'true';
	
	switch (t) {
		case 0:
			break;
		case 1:
			document.getElementById('themebtn').innerHTML = '<i class="fa fa-fw fa-sun-o"> </i>';
			break;
		case 2:
			document.getElementById('themebtn').innerHTML = '<i class="fa fa-fw fa-moon-o"> </i>';
			break;
	}
	
	if (document.getElementById('themebtn').innerHTML == '<i class="fa fa-fw fa-moon-o"> </i>'){
		//Set Dark Mode
		
		document.documentElement.style.setProperty("--col-bg-main", "black");
		document.documentElement.style.setProperty("--col-text", "#8c8c8c");
		document.documentElement.style.setProperty("--col-card-bg", "#222");
		document.documentElement.style.setProperty("--col-card-tab", "#333");
		document.documentElement.style.setProperty("--col-border1", "#666");
		
		document.getElementById('themebtn').innerHTML = '<i class="fa fa-fw fa-sun-o"> </i>';
		document.getElementById("homeSvg").style.fill = "#8c8c8c";
		
		//Flex Images
		(document.querySelectorAll('.flex img, .flex video[canBeDark]')||[]).forEach(function(img){
			img.classList.add('dark');
		});
		if (s == 'true'){
			setPref("dark_mode", true);
			console.log("Saved Dark Mode");
		}
		
	} else {
		//Set Light Mode
		
		document.documentElement.style.setProperty("--col-bg-main", "white");
		document.documentElement.style.setProperty("--col-text", "black");
		document.documentElement.style.setProperty("--col-card-bg", "#f8f8f8");
		document.documentElement.style.setProperty("--col-card-tab", "#eee");
		document.documentElement.style.setProperty("--col-border1", "#ddd");
		
		document.getElementById('themebtn').innerHTML = '<i class="fa fa-fw fa-moon-o"> </i>';
		document.getElementById("homeSvg").style.fill = "black";
		
		//Flex Images
		(document.querySelectorAll('.flex img, .flex video[canBeDark]')||[]).forEach(function(img){
			img.classList.remove('dark');
		});	
		if (s == 'true'){
			setPref("dark_mode", false);
			console.log("Saved Light Mode");
		}
	}
	(document.getElementById('themebtn2')||{}).innerHTML = document.getElementById('themebtn').innerHTML;
}

//Access Cookies

function setCookie(cname,cvalue,exdays) {
	var d = new Date();
	d.setTime(d.getTime() + (exdays*24*60*60*1000));
	var expires = "expires=" + d.toGMTString();
	document.cookie = cname + "=" + cvalue + ";" + expires + ";path=/";
}

function getCookie(cname) {
	var name = cname + "=";
	var decodedCookie = decodeURIComponent(document.cookie);
	var ca = decodedCookie.split(';');
	for( var i = 0; i < ca.length; i++ ){
		var c = ca[i];
		while (c.charAt(0) == ' ') {
			c = c.substring(1);
		}
		if (c.indexOf(name) == 0) {
			return c.substring(name.length, c.length);
		}
	}
	return "";
}

function checkCookie(cname) {
	var user = getCookie(cname);
	if (user != "") {
		alert("Welcome again " + user);
	} else {
		user = prompt("Please enter your name:","");
		if (user != "" && user != null) {
			setCookie(cname, user, 30);
		}
	}
}

